package com.banthi.androiddemos.contactRecyclerView;

import static android.util.Log.d;

import static androidx.core.content.ContextCompat.startActivity;

import androidx.appcompat.app.AppCompatActivity;
import androidx.recyclerview.widget.GridLayoutManager;
import androidx.recyclerview.widget.RecyclerView;

import android.annotation.SuppressLint;
import android.content.Intent;
import android.os.Bundle;
import android.util.Log;
import android.view.View;
import android.widget.Button;

import com.banthi.androiddemos.Fonts;
import com.banthi.androiddemos.R;

import java.util.ArrayList;

public class ContactRecyclerView extends AppCompatActivity {
    private Button showListBtn;
    private RecyclerView peopleList;
    private ArrayList<Contact> contacts;

    @SuppressLint("MissingInflatedId")
    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_contact_recyclerview);

        showListBtn = findViewById(R.id.rvListBtn);
        peopleList = findViewById(R.id.rvList);

        contacts = new ArrayList<>();
        contacts.add(new Contact("Banthapo Chabwera", "088676676", "b@u.com", "https://media.istockphoto.com/id/1442556244/photo/portrait-of-young-beautiful-woman-with-perfect-smooth-skin-isolated-over-white-background.jpg?s=612x612&w=0&k=20&c=4S7HufG4HDXznwuxFdliWndEAcWGKGvgqC45Ig0Zqog="));
        contacts.add(new Contact("Banthapo Nyanja", "0990909009", "n@u.com", "data:image/jpeg;base64,"));
        contacts.add(new Contact("Banthapo Kalira", "0887665553", "k@u.com", "data:image/jpeg;base64,"));
        contacts.add(new Contact("Banthapo Gawa", "0996755333", "g@u.com", "data:image/jpeg;base64,"));
        contacts.add(new Contact("Banthapo Flower", "098989877565", "f@u.com", "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTTVNqA_YQeRiImRTPIJrRAkV10MihLhaERgXlyUbFGj1HT6e6hjmiiMuo&s"));

        ContactAdapter adapter = new ContactAdapter(ContactRecyclerView.this);
        adapter.setContacts(contacts);

        peopleList.setAdapter(adapter);
//        peopleList.setLayoutManager(new LinearLayoutManager(this));
//        peopleList.setLayoutManager(new LinearLayoutManager(this, LinearLayoutManager.HORIZONTAL, false));
        peopleList.setLayoutManager(new GridLayoutManager(this, 2));

        showListBtn.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View v) {
//                Log.d("Visibility", "List is " +  peopleList.getVisibility());
                if (peopleList.getVisibility() == View.VISIBLE) {
                    peopleList.setVisibility(View.GONE);
                    showListBtn.setText("Show List");
                } else if (peopleList.getVisibility() == View.GONE) {
                    peopleList.setVisibility(View.VISIBLE);
                    showListBtn.setText("Hide List");
                }
            }
        });
    }

    public void handleBtn(View v){
        Intent intent = new Intent(ContactRecyclerView.this, Fonts.class);
        startActivity(intent);
    }
}